INSERT INTO BANK_INFOS
(BANK_ID, BALANCE, DIFFERENCE, ACCOUNT_U)
VALUES	(
		#{bank_id}
	,	(
			SELECT	BALANCE
			FROM	BANK
			WHERE	BANK_ID = #{bank_id}
		)
	,	#{difference}*(-1)
	,	#{account_u}
	)

