mapper.select:

SELECT * FROM (

	SELECT ROWNUM r, q.* FROM (

		SELECT * FROM QNA

		ORDER BY REF DESC, STEP ASC
	) q
)
WHERE r BETWEEN #{startRow} AND #{lastRow}

mapper.insert:
INSERT INTO QNA (
	BOARDNUM, BOARDWRITER, BOARDTITLE, BOARDCONTENTS
	, BOARDCATEGORY, REF, STEP, DEPTH
)
VALUES (
	BOARD_SEQ.NEXTVAL, #{boardWriter}, #{boardTitle}, #{boardContents}
	, 'Q', BOARD_SEQ.CURRVAL, 0 , 0
)

INSERT INTO QNA (
BOARDNUM, BOARDWRITER, BOARDTITLE, BOARDCONTENTS
, BOARDCATEGORY, REF, STEP, DEPTH
)
VALUES (
	BOARD_SEQ.NEXTVAL, #{boardWriter}, #{boardTitle}, #{boardContents}
	, 'Q', #{ref}, #{step} , #{depth}
)

mapper.update:
UPDATE	QNA
SET		STEP = STEP + 1
WHERE	REF = #{ref}
		AND STEP > #{step}

UPDATE	QNA
SET		BOARDTITLE = #{boardTitle}, BOARDCONTENTS = #{boardContents}, UPDATEDATE = SYSDATE
WHERE	BOARDNUM = #{boardNum}

mapper.delete:
UPDATE	QNA
SET		DEL = 1
WHERE	BOARDNUM = #{boardNum}
